08-基金收益	N(16,2)	=获取估值指标(T日,"901基金本日收益")+IIF(AND(获取工作日(T日)=T日,获取工作日(T日,1)<>DATEADD("D",1,T日)),SUME(1,DATEDIFF("D",T日,DATEADD("D",-1,获取工作日(T日,1))),"获取估值指标(DATEADD('D',V_X,T日),'901基金本日收益')"),0);
09-万份基金收益	N(9,4)	=获取估值指标(T日,"902每万份基金收益");
10-年收益率	N(9,4)	=REPLACE(获取估值指标(T日,"907基金七日收益率"),"%","");
11-销售服务费	N(16,2)	=获取科目发生额(获取科目("应付销售费",TRUE),T日,"D");
12-累计单位净值	N(16,4)	=1;

